import { createTheme } from '@mui/material/styles';

export const theme = createTheme({
    palette: {
        primary: {
            main: '#4CAF50', // 明るい緑色（信頼感と安心感を演出）
            light: '#81C784',
            dark: '#388E3C',
        },
        secondary: {
            main: '#FFA000', // オレンジ（アクセントカラー）
        },
        background: {
            default: '#F5F5F5',
            paper: '#FFFFFF',
        },
    },
    typography: {
        fontFamily: [
            '-apple-system',
            'BlinkMacSystemFont',
            '"Segoe UI"',
            'Roboto',
            '"Noto Sans JP"',
            'sans-serif',
        ].join(','),
        h4: {
            fontWeight: 600,
            fontSize: '1.75rem', // モバイルでも読みやすいサイズ
        },
        body1: {
            fontSize: '1rem', // 基本テキストサイズ
            lineHeight: 1.8,
        },
    },
    components: {
        MuiButton: {
            styleOverrides: {
                root: {
                    fontSize: '1.1rem',
                    padding: '12px 24px',
                    borderRadius: '8px',
                },
            },
        },
        MuiTextField: {
            styleOverrides: {
                root: {
                    '& .MuiInputBase-input': {
                        fontSize: '1.1rem',
                        padding: '16px 14px',
                    },
                },
            },
        },
    },
});
