import React, { useState } from 'react';
import {
    Box,
    TextField,
    Button,
    Typography,
    CircularProgress,
    Alert,
    styled,
    MenuItem,
    Select,
    FormControl,
    InputLabel,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../../utils/supabaseClient';

// スタイル付きコンポーネント
const PageWrapper = styled(Box)({
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
    padding: '20px',
});

const FormContainer = styled(Box)(({ theme }) => ({
    width: '100%',
    maxWidth: '500px',
    backgroundColor: 'rgba(255, 255, 255, 0.95)',
    borderRadius: '20px',
    padding: '40px',
    boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.1)',
    backdropFilter: 'blur(8.5px)',
    border: '1px solid rgba(255, 255, 255, 0.18)',
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
    marginBottom: theme.spacing(2),
    '& .MuiOutlinedInput-root': {
        borderRadius: '10px',
        transition: 'all 0.3s ease-in-out',
        '&:hover': {
            transform: 'translateY(-1px)',
        },
        '&.Mui-focused': {
            transform: 'translateY(-1px)',
            boxShadow: '0 4px 20px 0 rgba(0,0,0,0.1)',
        },
    },
}));

const StyledFormControl = styled(FormControl)(({ theme }) => ({
    marginBottom: theme.spacing(2),
    width: '100%',
    '& .MuiOutlinedInput-root': {
        borderRadius: '10px',
        transition: 'all 0.3s ease-in-out',
        '&:hover': {
            transform: 'translateY(-1px)',
        },
        '&.Mui-focused': {
            transform: 'translateY(-1px)',
            boxShadow: '0 4px 20px 0 rgba(0,0,0,0.1)',
        },
    },
}));

const RegisterButton = styled(Button)(({ theme }) => ({
    borderRadius: '10px',
    padding: '12px',
    fontSize: '16px',
    textTransform: 'none',
    background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
    boxShadow: '0 3px 5px 2px rgba(33, 203, 243, .3)',
    transition: 'all 0.3s ease-in-out',
    '&:hover': {
        transform: 'translateY(-2px)',
        boxShadow: '0 6px 10px 2px rgba(33, 203, 243, .3)',
    },
}));

const LinkButton = styled(Button)({
    textTransform: 'none',
    fontSize: '14px',
    color: '#666',
    '&:hover': {
        color: '#000',
        background: 'transparent',
    },
});

export default function Register() {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [formData, setFormData] = useState({
        nameKana: '',
        userId: '',
        email: '',
        password: '',
        confirmPassword: '',
        referrerId: '',
        usdtAddress: '',
        walletType: '',
    });
    const [fieldErrors, setFieldErrors] = useState({});

    const validateField = (name, value) => {
        let error = '';
        switch (name) {
            case 'nameKana':
                if (!value) {
                    error = '名前（カタカナ）は必須です';
                } else if (!/^[\u30A0-\u30FF0-9a-zA-Z\s]+$/.test(value)) {
                    error = 'カタカナと半角英数字のみ使用できます';
                }
                break;
            case 'userId':
                if (!value) {
                    error = 'ユーザーIDは必須です';
                } else if (!/^[a-zA-Z0-9]{6,}$/.test(value)) {
                    error = 'ユーザーIDは半角英数字6文字以上で入力してください';
                }
                break;
            case 'email':
                if (!value) {
                    error = 'メールアドレスは必須です';
                } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) {
                    error = '有効なメールアドレスを入力してください';
                }
                break;
            case 'password':
                if (!value) {
                    error = 'パスワードは必須です';
                }
                break;
            case 'confirmPassword':
                if (!value) {
                    error = 'パスワード（確認）は必須です';
                } else if (value !== formData.password) {
                    error = 'パスワードが一致しません';
                }
                break;
            case 'referrerId':
                if (!value) {
                    error = '紹介者IDは必須です';
                }
                break;
            case 'usdtAddress':
                if (value && !/^0x[a-fA-F0-9]+$/.test(value)) {
                    error = '有効なUSDTアドレス（BEP20）を入力してください';
                }
                break;
            default:
                break;
        }
        return error;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));

        const error = validateField(name, value);
        setFieldErrors(prev => ({
            ...prev,
            [name]: error
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // すべてのフィールドのバリデーション
        const errors = {};
        Object.keys(formData).forEach(field => {
            const error = validateField(field, formData[field]);
            if (error) errors[field] = error;
        });

        if (Object.keys(errors).length > 0) {
            setFieldErrors(errors);
            return;
        }

        try {
            setLoading(true);
            setError('');

            // ユーザーID重複チェック
            const { data: existingUser } = await supabase
                .from('profiles')
                .select('user_id')
                .eq('user_id', formData.userId)
                .single();

            if (existingUser) {
                setError('このユーザーIDは既に使用されています');
                return;
            }

            // 紹介者ID存在チェック
            const { data: referrer } = await supabase
                .from('profiles')
                .select('user_id')
                .eq('user_id', formData.referrerId)
                .single();

            if (!referrer) {
                setError('指定された紹介者IDは存在しません');
                return;
            }

            // ユーザー登録
            const { data, error: signUpError } = await supabase.auth.signUp({
                email: formData.email,
                password: formData.password,
            });

            if (signUpError) throw signUpError;

            // プロフィール作成
            if (data?.user) {
                const { error: profileError } = await supabase
                    .from('profiles')
                    .insert([
                        {
                            id: data.user.id,
                            name_kana: formData.nameKana,
                            user_id: formData.userId,
                            email: formData.email,
                            referrer_id: formData.referrerId,
                            usdt_address: formData.usdtAddress || null,
                            wallet_type: formData.walletType || null,
                            user_type: 'user'
                        }
                    ]);

                if (profileError) throw profileError;
            }

            alert('登録が完了しました。メールを確認してアカウントを有効化してください。');
            navigate('/');

        } catch (err) {
            setError(err.message);
            console.error('Error:', err.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <PageWrapper>
            <FormContainer>
                <Typography
                    variant="h4"
                    component="h1"
                    gutterBottom
                    sx={{
                        textAlign: 'center',
                        fontWeight: 700,
                        color: '#1a237e',
                        marginBottom: 4,
                        fontSize: '2rem',
                    }}
                >
                    新規登録
                </Typography>

                {error && (
                    <Alert
                        severity="error"
                        sx={{
                            marginBottom: 2,
                            borderRadius: 2,
                        }}
                    >
                        {error}
                    </Alert>
                )}

                <Box component="form" onSubmit={handleSubmit}>
                    <StyledTextField
                        fullWidth
                        label="名前（カタカナ）"
                        name="nameKana"
                        required
                        value={formData.nameKana}
                        onChange={handleChange}
                        error={!!fieldErrors.nameKana}
                        helperText={fieldErrors.nameKana}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label="希望ユーザーID"
                        name="userId"
                        required
                        value={formData.userId}
                        onChange={handleChange}
                        error={!!fieldErrors.userId}
                        helperText={fieldErrors.userId}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label="メールアドレス"
                        name="email"
                        type="email"
                        required
                        value={formData.email}
                        onChange={handleChange}
                        error={!!fieldErrors.email}
                        helperText={fieldErrors.email}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label="パスワード"
                        name="password"
                        type="password"
                        required
                        value={formData.password}
                        onChange={handleChange}
                        error={!!fieldErrors.password}
                        helperText={fieldErrors.password}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label="パスワード（確認）"
                        name="confirmPassword"
                        type="password"
                        required
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        error={!!fieldErrors.confirmPassword}
                        helperText={fieldErrors.confirmPassword}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label="紹介者ID"
                        name="referrerId"
                        required
                        value={formData.referrerId}
                        onChange={handleChange}
                        error={!!fieldErrors.referrerId}
                        helperText={fieldErrors.referrerId}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label="USDTアドレス（BEP20）"
                        name="usdtAddress"
                        value={formData.usdtAddress}
                        onChange={handleChange}
                        error={!!fieldErrors.usdtAddress}
                        helperText={fieldErrors.usdtAddress}
                        disabled={loading}
                    />

                    <StyledFormControl>
                        <InputLabel id="wallet-type-label">ウォレットタイプ</InputLabel>
                        <Select
                            labelId="wallet-type-label"
                            name="walletType"
                            value={formData.walletType}
                            label="ウォレットタイプ"
                            onChange={handleChange}
                            disabled={loading}
                        >
                            <MenuItem value="">選択してください</MenuItem>
                            <MenuItem value="evo">EVOカード</MenuItem>
                            <MenuItem value="other">その他</MenuItem>
                        </Select>
                    </StyledFormControl>

                    <RegisterButton
                        fullWidth
                        type="submit"
                        variant="contained"
                        disabled={loading}
                        sx={{ mt: 3, mb: 2 }}
                    >
                        {loading ? (
                            <CircularProgress size={24} color="inherit" />
                        ) : (
                            '登録する'
                        )}
                    </RegisterButton>

                    <Box sx={{
                        display: 'flex',
                        justifyContent: 'center',
                        mt: 2
                    }}>
                        <LinkButton
                            onClick={() => navigate('/')}
                            variant="text"
                        >
                            ログインへ戻る
                        </LinkButton>
                    </Box>
                </Box>
            </FormContainer>
        </PageWrapper>
    );
}
