import React, { useState } from 'react';
import {
    Box,
    TextField,
    Button,
    Typography,
    CircularProgress,
    Alert,
    styled,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../../utils/supabaseClient';

const PageWrapper = styled(Box)({
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
    padding: '20px',
});

const FormContainer = styled(Box)(({ theme }) => ({
    width: '100%',
    maxWidth: '400px',
    backgroundColor: 'rgba(255, 255, 255, 0.95)',
    borderRadius: '20px',
    padding: '40px',
    boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.1)',
    backdropFilter: 'blur(8.5px)',
    border: '1px solid rgba(255, 255, 255, 0.18)',
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
    marginBottom: theme.spacing(2),
    '& .MuiOutlinedInput-root': {
        borderRadius: '10px',
        transition: 'all 0.3s ease-in-out',
        '&:hover': {
            transform: 'translateY(-1px)',
        },
        '&.Mui-focused': {
            transform: 'translateY(-1px)',
            boxShadow: '0 4px 20px 0 rgba(0,0,0,0.1)',
        },
    },
}));

const LoginButton = styled(Button)(({ theme }) => ({
    borderRadius: '10px',
    padding: '12px',
    fontSize: '16px',
    textTransform: 'none',
    background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
    boxShadow: '0 3px 5px 2px rgba(33, 203, 243, .3)',
    transition: 'all 0.3s ease-in-out',
    '&:hover': {
        transform: 'translateY(-2px)',
        boxShadow: '0 6px 10px 2px rgba(33, 203, 243, .3)',
    },
}));

const LinkButtonContainer = styled(Box)({
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    gap: '12px',
    marginTop: '20px',
});

const MainLinkButton = styled(Button)({
    textTransform: 'none',
    fontSize: '16px',
    color: '#666',
    width: '100%',
    padding: '10px',
    '&:hover': {
        color: '#2196F3',
        background: 'rgba(33, 150, 243, 0.08)',
    },
});

const AdminLinkButton = styled(Button)({
    textTransform: 'none',
    fontSize: '12px',
    color: '#888',
    marginTop: '10px',
    '&:hover': {
        color: '#2196F3',
        background: 'transparent',
    },
});

export default function Login() {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [formData, setFormData] = useState({
        identifier: '', // メールアドレスまたはユーザーID
        password: '',
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            setLoading(true);
            setError('');

            let emailToUse = formData.identifier;

            // メールアドレス形式でない場合は、ユーザーIDとしてメールアドレスを検索
            if (!formData.identifier.includes('@')) {
                const { data: userData, error: userError } = await supabase
                    .from('profiles')
                    .select('email')
                    .eq('user_id', formData.identifier)
                    .single();

                if (userError || !userData) {
                    throw new Error('ユーザーIDが見つかりません');
                }
                emailToUse = userData.email;
            }

            const { data, error } = await supabase.auth.signInWithPassword({
                email: emailToUse,
                password: formData.password,
            });

            if (error) throw error;

            // ログイン成功後のリダイレクト
            navigate('/dashboard');

        } catch (error) {
            setError('ログインに失敗しました：' + error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <PageWrapper>
            <FormContainer>
                <Typography
                    variant="h4"
                    component="h1"
                    gutterBottom
                    sx={{
                        textAlign: 'center',
                        fontWeight: 600,
                        color: '#1a237e',
                        marginBottom: 4,
                        fontSize: '2rem'
                    }}
                >
                    ログイン
                </Typography>

                {error && (
                    <Alert
                        severity="error"
                        sx={{ marginBottom: 2, borderRadius: 2 }}
                    >
                        {error}
                    </Alert>
                )}

                <Box component="form" onSubmit={handleSubmit}>
                    <StyledTextField
                        fullWidth
                        label="メールアドレスまたはユーザーID"
                        name="identifier"
                        required
                        value={formData.identifier}
                        onChange={handleChange}
                        disabled={loading}
                        InputProps={{
                            sx: { height: '50px' }
                        }}
                    />

                    <StyledTextField
                        fullWidth
                        label="パスワード"
                        name="password"
                        type="password"
                        required
                        value={formData.password}
                        onChange={handleChange}
                        disabled={loading}
                        InputProps={{
                            sx: { height: '50px' }
                        }}
                    />

                    <Button
                        fullWidth
                        type="submit"
                        variant="contained"
                        disabled={loading}
                        sx={{
                            mt: 3,
                            mb: 2,
                            height: '50px',
                            background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
                        }}
                    >
                        {loading ? (
                            <CircularProgress size={24} color="inherit" />
                        ) : (
                            'ログイン'
                        )}
                    </Button>

                    <Button
                        fullWidth
                        onClick={() => navigate('/register')}
                        variant="text"
                        sx={{ color: '#666' }}
                    >
                        新規登録はこちら
                    </Button>
                </Box>
            </FormContainer>
        </PageWrapper>
    );
}
