import React, { useEffect, useState } from 'react';
import {
    Box,
    Container,
    Typography,
    Paper,
    Grid,
    TextField,
    Button,
    Alert,
    Divider,
    styled,
} from '@mui/material';
import Header from '../../components/Layout/Header';
import { supabase } from '../../utils/supabaseClient';

const StyledPaper = styled(Paper)(({ theme }) => ({
    padding: theme.spacing(3),
    margin: theme.spacing(2, 0),
    borderRadius: theme.spacing(2),
    boxShadow: '0 3px 5px 2px rgba(0, 0, 0, .03)',
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
    marginBottom: theme.spacing(2),
}));

export default function UserDashboard() {
    const [profile, setProfile] = useState(null);
    const [editing, setEditing] = useState(false);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(null);
    const [editedProfile, setEditedProfile] = useState({});

    useEffect(() => {
        fetchProfile();
    }, []);

    const fetchProfile = async () => {
        try {
            const { data: { user } } = await supabase.auth.getUser();
            if (!user) throw new Error('User not found');

            const { data, error } = await supabase
                .from('profiles')
                .select('*')
                .eq('id', user.id)
                .single();

            if (error) throw error;
            setProfile(data);
            setEditedProfile(data);
        } catch (error) {
            console.error('Error fetching profile:', error);
            setError(error.message);
        }
    };

    const handleEdit = () => {
        setEditing(true);
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedProfile(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleCancel = () => {
        setEditedProfile(profile);
        setEditing(false);
        setError(null);
    };

    const validateProfile = () => {
        if (!editedProfile.name_kana?.match(/^[ァ-ヶー0-9A-Za-z\s]*$/)) {
            throw new Error('名前は全角カタカナと半角英数字のみ使用可能です');
        }
        if (!editedProfile.phone?.match(/^\d+$/)) {
            throw new Error('電話番号は数字のみで入力してください');
        }
        if (editedProfile.usdt_address && !editedProfile.usdt_address.match(/^0x[a-fA-F0-9]+$/)) {
            throw new Error('無効なUSDTアドレスです');
        }
    };

    const handleSave = async () => {
        try {
            setLoading(true);
            setError(null);

            // バリデーション
            validateProfile();

            const { error } = await supabase
                .from('profiles')
                .update({
                    name_kana: editedProfile.name_kana,
                    phone: editedProfile.phone,
                    usdt_address: editedProfile.usdt_address,
                    wallet_type: editedProfile.wallet_type,
                })
                .eq('id', profile.id);

            if (error) throw error;

            setSuccess('プロフィールを更新しました');
            setProfile(editedProfile);
            setEditing(false);
            setTimeout(() => setSuccess(null), 3000);
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box>
            <Header />
            <Container maxWidth="lg" sx={{ mt: 10, mb: 4 }}>
                <StyledPaper>
                    <Typography variant="h5" gutterBottom>
                        プロフィール
                    </Typography>
                    <Divider sx={{ mb: 3 }} />

                    {error && (
                        <Alert severity="error" sx={{ mb: 2 }}>
                            {error}
                        </Alert>
                    )}

                    {success && (
                        <Alert severity="success" sx={{ mb: 2 }}>
                            {success}
                        </Alert>
                    )}

                    <Grid container spacing={2}>
                        <Grid item xs={12} md={6}>
                            <StyledTextField
                                fullWidth
                                label="ユーザーID"
                                value={profile?.user_id || ''}
                                disabled
                            />

                            <StyledTextField
                                fullWidth
                                label="メールアドレス"
                                value={profile?.email || ''}
                                disabled
                            />

                            <StyledTextField
                                fullWidth
                                label="名前（カタカナ）"
                                name="name_kana"
                                value={editing ? editedProfile.name_kana || '' : profile?.name_kana || ''}
                                onChange={handleChange}
                                disabled={!editing}
                            />

                            <StyledTextField
                                fullWidth
                                label="電話番号"
                                name="phone"
                                value={editing ? editedProfile.phone || '' : profile?.phone || ''}
                                onChange={handleChange}
                                disabled={!editing}
                            />

                            <StyledTextField
                                fullWidth
                                label="USDTアドレス（BEP20）"
                                name="usdt_address"
                                value={editing ? editedProfile.usdt_address || '' : profile?.usdt_address || ''}
                                onChange={handleChange}
                                disabled={!editing}
                            />

                            <StyledTextField
                                fullWidth
                                label="紹介者ID"
                                value={profile?.referral_id || ''}
                                disabled
                            />
                        </Grid>
                    </Grid>

                    <Box sx={{ mt: 3 }}>
                        {!editing ? (
                            <Button
                                variant="contained"
                                onClick={handleEdit}
                                disabled={loading}
                            >
                                編集する
                            </Button>
                        ) : (
                            <Box sx={{ display: 'flex', gap: 2 }}>
                                <Button
                                    variant="contained"
                                    onClick={handleSave}
                                    disabled={loading}
                                >
                                    保存する
                                </Button>
                                <Button
                                    variant="outlined"
                                    onClick={handleCancel}
                                    disabled={loading}
                                >
                                    キャンセル
                                </Button>
                            </Box>
                        )}
                    </Box>
                </StyledPaper>
            </Container>
        </Box>
    );
}
