import React, { useState } from 'react';
import {
    Box,
    Paper,
    Typography,
    TextField,
    Button,
    styled,
    Alert,
    CircularProgress,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../../utils/supabaseClient';

const LoginWrapper = styled(Box)({
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: '#f8f9fa',
    padding: '20px',
});

const LoginBox = styled(Paper)({
    width: '100%',
    maxWidth: '400px',
    padding: '40px',
    textAlign: 'center',
});

const StyledTextField = styled(TextField)({
    marginBottom: '16px',
    '& .MuiInputBase-input': {
        padding: '12px',
    },
    '& .MuiOutlinedInput-root': {
        backgroundColor: '#ffffff',
    },
});

const StyledButton = styled(Button)({
    padding: '12px 0',
    fontWeight: 500,
    '&.MuiButton-contained': {
        backgroundColor: '#0f172a',
        '&:hover': {
            backgroundColor: '#1e293b',
        },
    },
    '&.MuiButton-text': {
        color: '#6b7280',
        '&:hover': {
            backgroundColor: 'rgba(0, 0, 0, 0.04)',
        },
    },
});

const TestAccountBox = styled(Box)({
    marginTop: '24px',
    padding: '16px',
    backgroundColor: '#f3f4f6',
    borderRadius: '8px',
    fontSize: '0.875rem',
    color: '#666',
    textAlign: 'left',
});

export default function Login() {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [credentials, setCredentials] = useState({
        identifier: '',
        password: '',
    });

    const handleChange = (e) => {
        setCredentials({
            ...credentials,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            let emailToUse = credentials.identifier;

            // メールアドレス形式でない場合は、ユーザーIDとしてメールアドレスを検索
            if (!credentials.identifier.includes('@')) {
                const { data: userData, error: userError } = await supabase
                    .from('profiles')
                    .select('email')
                    .eq('user_id', credentials.identifier)
                    .single();

                if (userError || !userData) {
                    throw new Error('ユーザーIDが見つかりません');
                }
                emailToUse = userData.email;
            }

            const { data: { user }, error: signInError } = await supabase.auth.signInWithPassword({
                email: emailToUse,
                password: credentials.password,
            });

            if (signInError) throw signInError;

            // ユーザータイプの確認
            const { data: profileData, error: profileError } = await supabase
                .from('profiles')
                .select('user_type')
                .eq('id', user.id)
                .single();

            if (profileError) throw profileError;

            // ユーザータイプに応じてリダイレクト
            navigate(profileData.user_type === 'admin' ? '/admin/dashboard' : '/dashboard');

        } catch (error) {
            setError('ログインに失敗しました：' + error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <LoginWrapper>
            <LoginBox>
                <Typography variant="h5" sx={{ mb: 4 }}>
                    ユーザーログイン
                </Typography>

                {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                        {error}
                    </Alert>
                )}

                <Box component="form" onSubmit={handleSubmit}>
                    <StyledTextField
                        fullWidth
                        placeholder="ユーザーIDを入力"
                        name="identifier"
                        value={credentials.identifier}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        type="password"
                        placeholder="パスワードを入力"
                        name="password"
                        value={credentials.password}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <StyledButton
                        fullWidth
                        type="submit"
                        variant="contained"
                        disabled={loading}
                        sx={{ mb: 2 }}
                    >
                        {loading ? (
                            <CircularProgress size={24} color="inherit" />
                        ) : (
                            'ログイン'
                        )}
                    </StyledButton>

                    <StyledButton
                        fullWidth
                        variant="text"
                        onClick={() => navigate('/register')}
                        disabled={loading}
                        sx={{ mb: 2 }}
                    >
                        新規登録
                    </StyledButton>

                    <StyledButton
                        fullWidth
                        variant="text"
                        onClick={() => navigate('/admin')}
                        disabled={loading}
                        sx={{
                            backgroundColor: '#f3f4f6',
                            '&:hover': {
                                backgroundColor: '#e5e7eb',
                            }
                        }}
                    >
                        管理者ログインはこちら
                    </StyledButton>

                    <TestAccountBox>
                        <Typography variant="body2" gutterBottom>
                            テスト用アカウント：
                        </Typography>
                        <Typography variant="body2">
                            一般ユーザー：<br />
                            ID: user123 / パスワード: pass123
                        </Typography>
                        <Typography variant="body2" sx={{ mt: 1 }}>
                            管理者：<br />
                            ID: admin123 / パスワード: admin123
                        </Typography>
                    </TestAccountBox>
                </Box>
            </LoginBox>
        </LoginWrapper>
    );
}
