import React, { useState, useEffect } from 'react';
import {
    Box,
    TextField,
    Button,
    Typography,
    CircularProgress,
    Alert,
    styled,
    FormControl,
    Select,
    MenuItem,
    InputLabel,
} from '@mui/material';
import { useNavigate } from 'react-router-dom'; f
import { supabase } from '../../utils/supabaseClient';

const PageWrapper = styled(Box)({
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
    padding: '20px',
});

const FormContainer = styled(Box)(({ theme }) => ({
    width: '100%',
    maxWidth: '400px',
    backgroundColor: 'rgba(255, 255, 255, 0.95)',
    borderRadius: '20px',
    padding: '40px',
    boxShadow: '0 8px 32px 0 rgba(31, 38, 135, 0.1)',
    backdropFilter: 'blur(8.5px)',
    border: '1px solid rgba(255, 255, 255, 0.18)',
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
    marginBottom: theme.spacing(2),
    '& .MuiOutlinedInput-root': {
        borderRadius: '10px',
        transition: 'all 0.3s ease-in-out',
        '&:hover': {
            transform: 'translateY(-1px)',
        },
        '&.Mui-focused': {
            transform: 'translateY(-1px)',
            boxShadow: '0 4px 20px 0 rgba(0,0,0,0.1)',
        },
    },
}));

const RegisterButton = styled(Button)(({ theme }) => ({
    borderRadius: '10px',
    padding: '12px',
    fontSize: '16px',
    textTransform: 'none',
    background: 'linear-gradient(45deg, #2196F3 30%, #21CBF3 90%)',
    boxShadow: '0 3px 5px 2px rgba(33, 203, 243, .3)',
    transition: 'all 0.3s ease-in-out',
    '&:hover': {
        transform: 'translateY(-2px)',
        boxShadow: '0 6px 10px 2px rgba(33, 203, 243, .3)',
    },
}));

const LoginLink = styled(Button)({
    textTransform: 'none',
    fontSize: '14px',
    color: '#666',
    '&:hover': {
        color: '#2196F3',
        background: 'rgba(33, 150, 243, 0.08)',
    },
});

export default function Register() {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [success, setSuccess] = useState(false);
    const [error, setError] = useState('');
    const [userIdExists, setUserIdExists] = useState(false);
    const [referralValid, setReferralValid] = useState(true);

    const [formData, setFormData] = useState({
        nameKana: '',
        userId: '',
        email: '',
        password: '',
        phone: '',
        referralId: '',
        usdtAddress: '',
        walletType: ''
    });

    // バリデーション関数
    const validateNameKana = (value) => {
        return /^[ァ-ヶー0-9A-Za-z\s]*$/.test(value);
    };

    const validateUserId = (value) => {
        return /^[A-Za-z0-9]{6,}$/.test(value);
    };

    const validateEmail = (value) => {
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value);
    };

    const validatePhone = (value) => {
        return /^\d+$/.test(value);
    };

    const validateUsdtAddress = (value) => {
        if (!value) return true; // 任意フィールド
        return /^0x[a-fA-F0-9]+$/.test(value);
    };

    // ユーザーID重複チェック
    useEffect(() => {
        const checkUserId = async () => {
            if (validateUserId(formData.userId)) {
                const { data, error } = await supabase
                    .from('profiles')
                    .select('user_id')
                    .eq('user_id', formData.userId);

                setUserIdExists(data && data.length > 0);
            }
        };
        checkUserId();
    }, [formData.userId]);

    // 紹介者IDの存在チェック
    useEffect(() => {
        const checkReferralId = async () => {
            if (formData.referralId) {
                const { data, error } = await supabase
                    .from('profiles')
                    .select('user_id')
                    .eq('user_id', formData.referralId);

                setReferralValid(data && data.length > 0);
            }
        };
        checkReferralId();
    }, [formData.referralId]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // バリデーションチェック
        if (!validateNameKana(formData.nameKana)) {
            setError('名前は全角カタカナと半角英数字のみ使用可能です');
            return;
        }
        if (!validateUserId(formData.userId)) {
            setError('ユーザーIDは半角英数字6文字以上で入力してください');
            return;
        }
        if (userIdExists) {
            setError('このユーザーIDは既に使用されています');
            return;
        }
        if (!validateEmail(formData.email)) {
            setError('有効なメールアドレスを入力してください');
            return;
        }
        if (!validatePhone(formData.phone)) {
            setError('電話番号は数字のみで入力してください');
            return;
        }
        if (!referralValid) {
            setError('無効な紹介者IDです');
            return;
        }
        if (formData.usdtAddress && !validateUsdtAddress(formData.usdtAddress)) {
            setError('無効なUSDTアドレスです');
            return;
        }

        try {
            setLoading(true);
            setError('');

            // Supabaseでユーザー認証を作成
            const { data: authData, error: authError } = await supabase.auth.signUp({
                email: formData.email,
                password: formData.password,
            });

            if (authError) throw authError;

            // プロファイルテーブルの更新
            const { error: profileError } = await supabase
                .from('profiles')
                .upsert({
                    id: authData.user.id,
                    name_kana: formData.nameKana,
                    user_id: formData.userId,
                    phone: formData.phone,
                    referral_id: formData.referralId,
                    usdt_address: formData.usdtAddress,
                    wallet_type: formData.walletType,
                    user_type: 'user'
                });

            if (profileError) throw profileError;

            setSuccess(true);

        } catch (error) {
            setError('登録に失敗しました：' + error.message);
            console.error('Error:', error);
        } finally {
            setLoading(false);
        }
    };

    return (
        <PageWrapper>
            <FormContainer>
                <Typography
                    variant="h4"
                    component="h1"
                    gutterBottom
                    sx={{
                        textAlign: 'center',
                        fontWeight: 600,
                        color: '#1a237e',
                        marginBottom: 4,
                        fontSize: '2rem'
                    }}
                >
                    新規登録
                </Typography>

                {error && (
                    <Alert severity="error" sx={{ marginBottom: 2, borderRadius: 2 }}>
                        {error}
                    </Alert>
                )}

                {success ? (
                    <Box>
                        <Alert severity="success" sx={{ marginBottom: 2, borderRadius: 2 }}>
                            登録が完了しました。確認メールをご確認ください。
                        </Alert>
                        <LoginLink
                            fullWidth
                            onClick={() => navigate('/')}
                            variant="text"
                        >
                            ログイン画面へ
                        </LoginLink>
                    </Box>
                ) : (
                    <Box component="form" onSubmit={handleSubmit}>
                        <StyledTextField
                            fullWidth
                            label="名前（カタカナ）"
                            name="nameKana"
                            required
                            value={formData.nameKana}
                            onChange={handleChange}
                            disabled={loading}
                            error={formData.nameKana && !validateNameKana(formData.nameKana)}
                            helperText={formData.nameKana && !validateNameKana(formData.nameKana) ?
                                "カタカナと半角英数字のみ使用可能です" : ""}
                        />

                        <StyledTextField
                            fullWidth
                            label="希望ユーザーID"
                            name="userId"
                            required
                            value={formData.userId}
                            onChange={handleChange}
                            disabled={loading}
                            error={userIdExists || (formData.userId && !validateUserId(formData.userId))}
                            helperText={userIdExists ? "このIDは既に使用されています" :
                                formData.userId && !validateUserId(formData.userId) ?
                                    "半角英数字6文字以上で入力してください" : ""}
                        />

                        <StyledTextField
                            fullWidth
                            label="メールアドレス"
                            name="email"
                            type="email"
                            required
                            value={formData.email}
                            onChange={handleChange}
                            disabled={loading}
                            error={formData.email && !validateEmail(formData.email)}
                            helperText={formData.email && !validateEmail(formData.email) ?
                                "有効なメールアドレスを入力してください" : ""}
                        />

                        <StyledTextField
                            fullWidth
                            label="パスワード"
                            name="password"
                            type="password"
                            required
                            value={formData.password}
                            onChange={handleChange}
                            disabled={loading}
                        />

                        <StyledTextField
                            fullWidth
                            label="電話番号（ハイフンなし）"
                            name="phone"
                            required
                            value={formData.phone}
                            onChange={handleChange}
                            disabled={loading}
                            error={formData.phone && !validatePhone(formData.phone)}
                            helperText={formData.phone && !validatePhone(formData.phone) ?
                                "数字のみで入力してください" : ""}
                        />

                        <StyledTextField
                            fullWidth
                            label="紹介者ID"
                            name="referralId"
                            required
                            value={formData.referralId}
                            onChange={handleChange}
                            disabled={loading}
                            error={formData.referralId && !referralValid}
                            helperText={formData.referralId && !referralValid ?
                                "無効な紹介者IDです" : ""}
                        />

                        <StyledTextField
                            fullWidth
                            label="USDTアドレス（BEP20）"
                            name="usdtAddress"
                            value={formData.usdtAddress}
                            onChange={handleChange}
                            disabled={loading}
                            error={formData.usdtAddress && !validateUsdtAddress(formData.usdtAddress)}
                            helperText={formData.usdtAddress && !validateUsdtAddress(formData.usdtAddress) ?
                                "0xで始まる有効なアドレスを入力してください" : ""}
                        />

                        <FormControl fullWidth sx={{ mb: 2 }}>
                            <InputLabel>ウォレットタイプ</InputLabel>
                            <Select
                                value={formData.walletType}
                                label="ウォレットタイプ"
                                name="walletType"
                                onChange={handleChange}
                            >
                                <MenuItem value="evo">EVOカード</MenuItem>
                                <MenuItem value="other">その他</MenuItem>
                            </Select>
                        </FormControl>

                        <RegisterButton
                            fullWidth
                            type="submit"
                            variant="contained"
                            disabled={loading}
                            sx={{ mt: 3, mb: 2 }}
                        >
                            {loading ? (
                                <CircularProgress size={24} color="inherit" />
                            ) : (
                                '登録する'
                            )}
                        </RegisterButton>

                        <LoginLink
                            fullWidth
                            onClick={() => navigate('/')}
                            variant="text"
                        >
                            すでにアカウントをお持ちの方
                        </LoginLink>
                    </Box>
                )}
            </FormContainer>
        </PageWrapper>
    );
}
