import React, { useState } from 'react';
import {
    Box,
    Paper,
    Typography,
    TextField,
    Button,
    styled,
    Alert,
    CircularProgress,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../../utils/supabaseClient';

const RegisterWrapper = styled(Box)({
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: '#f8f9fa',
    padding: '20px',
});

const RegisterBox = styled(Paper)({
    width: '100%',
    maxWidth: '400px',
    padding: '40px',
});

const StyledTextField = styled(TextField)({
    marginBottom: '16px',
    '& .MuiInputBase-input': {
        padding: '12px',
    },
    '& .MuiOutlinedInput-root': {
        backgroundColor: '#ffffff',
    },
});

const StyledButton = styled(Button)({
    padding: '12px 0',
    fontWeight: 500,
    '&.MuiButton-contained': {
        backgroundColor: '#0f172a',
        '&:hover': {
            backgroundColor: '#1e293b',
        },
    },
    '&.MuiButton-text': {
        color: '#6b7280',
        '&:hover': {
            backgroundColor: 'rgba(0, 0, 0, 0.04)',
        },
    },
});

const RequiredLabel = () => (
    <Typography component="span" color="error" sx={{ ml: 0.5 }}>
        *
    </Typography>
);

const WALLET_TYPES = [
    { value: 'evo', label: 'EVOカード' },
    { value: 'other', label: 'その他' },
];

export default function Register() {
    const navigate = useNavigate();
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [formData, setFormData] = useState({
        name_kana: '',
        user_id: '',
        email: '',
        password: '',
        confirmPassword: '',
        referral_id: '',
        usdt_address: '',
        wallet_type: '',
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const validateForm = () => {
        if (!formData.name_kana.match(/^[ァ-ヶー]+$/)) {
            throw new Error('名前は全角カタカナで入力してください');
        }
        if (!formData.email.match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/)) {
            throw new Error('有効なメールアドレスを入力してください');
        }
        if (formData.password !== formData.confirmPassword) {
            throw new Error('パスワードが一致しません');
        }
        if (formData.password.length < 6) {
            throw new Error('パスワードは6文字以上で入力してください');
        }
        if (!formData.wallet_type) {
            throw new Error('ウォレットタイプを選択してください');
        }
        if (formData.usdt_address && !formData.usdt_address.match(/^0x[a-fA-F0-9]{40}$/)) {
            throw new Error('無効なUSDTアドレスです');
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            validateForm();

            // メールアドレスとユーザーIDの重複チェック
            const { data: existingUser, error: checkError } = await supabase
                .from('profiles')
                .select('email, user_id')
                .or(`email.eq.${formData.email},user_id.eq.${formData.user_id}`)
                .single();

            if (existingUser) {
                throw new Error('このメールアドレスまたはユーザーIDは既に使用されています');
            }

            const { data: authData, error: authError } = await supabase.auth.signUp({
                email: formData.email,
                password: formData.password,
            });

            if (authError) throw authError;

            const { error: profileError } = await supabase
                .from('profiles')
                .insert([
                    {
                        id: authData.user.id,
                        name_kana: formData.name_kana,
                        user_id: formData.user_id,
                        email: formData.email,
                        referral_id: formData.referral_id,
                        usdt_address: formData.usdt_address || null,
                        wallet_type: formData.wallet_type,
                        user_type: 'user',
                    },
                ]);

            if (profileError) throw profileError;

            navigate('/register-success');
        } catch (error) {
            setError(error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <RegisterWrapper>
            <RegisterBox>
                <Typography variant="h5" gutterBottom sx={{ textAlign: 'center', mb: 4 }}>
                    新規ユーザー登録
                </Typography>

                {error && (
                    <Alert severity="error" sx={{ mb: 2 }}>
                        {error}
                    </Alert>
                )}

                <Box component="form" onSubmit={handleSubmit}>
                    <StyledTextField
                        fullWidth
                        label={<>名前（カタカナ）<RequiredLabel /></>}
                        name="name_kana"
                        value={formData.name_kana}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label={<>ユーザーID<RequiredLabel /></>}
                        name="user_id"
                        value={formData.user_id}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label={<>メールアドレス<RequiredLabel /></>}
                        name="email"
                        type="email"
                        value={formData.email}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label={<>パスワード<RequiredLabel /></>}
                        name="password"
                        type="password"
                        value={formData.password}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label={<>パスワード（確認）<RequiredLabel /></>}
                        name="confirmPassword"
                        type="password"
                        value={formData.confirmPassword}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <StyledTextField
                        fullWidth
                        label={<>紹介者ID<RequiredLabel /></>}
                        name="referral_id"
                        value={formData.referral_id}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <FormControl fullWidth sx={{ mb: 2 }}>
                        <InputLabel id="wallet-type-label">
                            ウォレットタイプ<RequiredLabel />
                        </InputLabel>
                        <Select
                            labelId="wallet-type-label"
                            name="wallet_type"
                            value={formData.wallet_type}
                            onChange={handleChange}
                            disabled={loading}
                            label="ウォレットタイプ"
                        >
                            {WALLET_TYPES.map((type) => (
                                <MenuItem key={type.value} value={type.value}>
                                    {type.label}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>

                    <StyledTextField
                        fullWidth
                        label="USDTアドレス（BEP20）"
                        name="usdt_address"
                        value={formData.usdt_address}
                        onChange={handleChange}
                        disabled={loading}
                    />

                    <StyledButton
                        fullWidth
                        type="submit"
                        variant="contained"
                        disabled={loading}
                        sx={{ mb: 2 }}
                    >
                        {loading ? (
                            <CircularProgress size={24} color="inherit" />
                        ) : (
                            '登録'
                        )}
                    </StyledButton>

                    <StyledButton
                        fullWidth
                        variant="text"
                        onClick={() => navigate('/')}
                        disabled={loading}
                    >
                        ログインページへ戻る
                    </StyledButton>
                </Box>
            </RegisterBox>
        </RegisterWrapper>
    );
}
