// src/components/Auth/Register.js
import React, { useState, useEffect } from 'react';
import {
    Box,
    Button,
    TextField,
    Typography,
    Container,
    Alert,
    Paper,
    CircularProgress,
    FormControl,
    InputLabel,
    Select,
    MenuItem
} from '@mui/material';
import { Link } from 'react-router-dom';
import { supabase } from '../../utils/supabaseClient';

export default function Register() {
    const [formData, setFormData] = useState({
        nameKana: '',
        userId: '',
        email: '',
        password: '',
        referralId: '',
        usdtAddress: '',
        walletType: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');
    const [isUserIdAvailable, setIsUserIdAvailable] = useState(true);
    const [isReferralIdValid, setIsReferralIdValid] = useState(false);

    // バリデーション用の正規表現
    const kanaRegex = /^[ァ-ヶー]*$/;
    const userIdRegex = /^[a-zA-Z0-9]{6,}$/;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const usdtAddressRegex = /^0x[a-fA-F0-9]+$/;

    // ユーザーID重複チェック
    useEffect(() => {
        const checkUserId = async () => {
            if (formData.userId.length >= 6) {
                const { data, error } = await supabase
                    .from('profiles')
                    .select('user_id')
                    .eq('user_id', formData.userId)
                    .single();

                setIsUserIdAvailable(!data);
            }
        };
        checkUserId();
    }, [formData.userId]);

    // 紹介者ID存在チェック
    useEffect(() => {
        const checkReferralId = async () => {
            if (formData.referralId) {
                const { data, error } = await supabase
                    .from('profiles')
                    .select('user_id')
                    .eq('user_id', formData.referralId)
                    .single();

                setIsReferralIdValid(!!data);
            }
        };
        checkReferralId();
    }, [formData.referralId]);

    const handleChange = (e) => {
        const { name, value } = e.target;

        // 入力値のバリデーション
        if (name === 'nameKana' && !kanaRegex.test(value)) return;
        if (name === 'userId' && value && !userIdRegex.test(value)) return;
        if (name === 'usdtAddress' && value && !usdtAddressRegex.test(value)) return;

        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        // 全体的なバリデーション
        if (!formData.nameKana || !kanaRegex.test(formData.nameKana)) {
            setError('名前は全角カタカナで入力してください');
            setLoading(false);
            return;
        }

        if (!formData.userId || !userIdRegex.test(formData.userId)) {
            setError('ユーザーIDは半角英数6文字以上で入力してください');
            setLoading(false);
            return;
        }

        if (!isUserIdAvailable) {
            setError('このユーザーIDは既に使用されています');
            setLoading(false);
            return;
        }

        if (!formData.email || !emailRegex.test(formData.email)) {
            setError('有効なメールアドレスを入力してください');
            setLoading(false);
            return;
        }

        if (!isReferralIdValid) {
            setError('有効な紹介者IDを入力してください');
            setLoading(false);
            return;
        }

        if (formData.usdtAddress && !usdtAddressRegex.test(formData.usdtAddress)) {
            setError('有効なUSDTアドレスを入力してください');
            setLoading(false);
            return;
        }

        try {
            // 1. 新規ユーザー登録
            const { data: { user }, error: signUpError } = await supabase.auth.signUp({
                email: formData.email,
                password: formData.password,
            });

            if (signUpError) throw signUpError;

            // 2. プロフィール作成
            const { error: profileError } = await supabase
                .from('profiles')
                .insert([
                    {
                        id: user.id,
                        name_kana: formData.nameKana,
                        user_id: formData.userId,
                        referral_id: formData.referralId,
                        usdt_address: formData.usdtAddress,
                        wallet_type: formData.walletType,
                        user_type: 'user',
                        created_at: new Date(),
                    }
                ]);

            if (profileError) throw profileError;

            setSuccessMessage('登録が完了しました。メールを確認してください。');

        } catch (error) {
            setError('登録に失敗しました: ' + error.message);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Container component="main" maxWidth="xs">
            <Box sx={{ marginTop: 8, marginBottom: 8 }}>
                <Paper elevation={3} sx={{ p: 4 }}>
                    <Typography component="h1" variant="h5" align="center" gutterBottom>
                        新規登録
                    </Typography>

                    {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
                    {successMessage && <Alert severity="success" sx={{ mb: 2 }}>{successMessage}</Alert>}

                    <Box component="form" onSubmit={handleSubmit}>
                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            label="名前（カタカナ）"
                            name="nameKana"
                            value={formData.nameKana}
                            onChange={handleChange}
                            error={formData.nameKana && !kanaRegex.test(formData.nameKana)}
                            helperText="全角カタカナで入力してください"
                        />

                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            label="希望ユーザーID"
                            name="userId"
                            value={formData.userId}
                            onChange={handleChange}
                            error={!isUserIdAvailable}
                            helperText={!isUserIdAvailable ? "このIDは既に使用されています" : "半角英数6文字以上"}
                        />

                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            label="メールアドレス"
                            name="email"
                            type="email"
                            value={formData.email}
                            onChange={handleChange}
                        />

                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            label="パスワード"
                            name="password"
                            type="password"
                            value={formData.password}
                            onChange={handleChange}
                        />

                        <TextField
                            margin="normal"
                            required
                            fullWidth
                            label="紹介者ID"
                            name="referralId"
                            value={formData.referralId}
                            onChange={handleChange}
                            error={formData.referralId && !isReferralIdValid}
                            helperText={formData.referralId && !isReferralIdValid ? "有効な紹介者IDを入力してください" : ""}
                        />

                        <TextField
                            margin="normal"
                            fullWidth
                            label="USDTアドレス（BEP20）"
                            name="usdtAddress"
                            value={formData.usdtAddress}
                            onChange={handleChange}
                            error={formData.usdtAddress && !usdtAddressRegex.test(formData.usdtAddress)}
                            helperText="0xから始まるアドレスを入力してください"
                        />

                        <FormControl fullWidth margin="normal">
                            <InputLabel>ウォレットタイプ</InputLabel>
                            <Select
                                value={formData.walletType}
                                label="ウォレットタイプ"
                                name="walletType"
                                onChange={handleChange}
                            >
                                <MenuItem value="evocard">EVOカード</MenuItem>
                                <MenuItem value="other">その他</MenuItem>
                            </Select>
                        </FormControl>

                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            sx={{ mt: 3, mb: 2 }}
                            disabled={loading}
                        >
                            {loading ? <CircularProgress size={24} /> : '登録'}
                        </Button>

                        <Box sx={{ textAlign: 'center' }}>
                            <Link to="/" style={{ textDecoration: 'none' }}>
                                <Typography color="primary">
                                    既にアカウントをお持ちの方はこちら
                                </Typography>
                            </Link>
                        </Box>
                    </Box>
                </Paper>
            </Box>
        </Container>
    );
}
